//why do we use expreess?
//for every post and get request we need to write so much code
//express came into the picture made easy to do
//intsall it
import express from "express";
const app=express();
const port=3000;

app.get('/',(req,res)=>{
    res.send("hello world");
})

//these aare called endpoints 
//local:3000/->these are by  by default works as a get method only;
app.get('/blog/:slug', (req, res) => {
    console.log("BLOG route hit");
    console.log("Slug received:", req.params.slug);
    res.send(`hello ${req.params.slug}`);
});

//:slug->is a parameter
// To test: visit http://localhost:3000/blog/your-slug-here
app.listen(port,()=>{
    console.log(`example app listening on the ${port}`)
})